const mongoose = require('mongoose')
const defaultImg = 'data:image/jpeg;base64,'

const userModel = mongoose.Schema({
  name: {
    type: String,
    required: true
  },
  email: {
    type: String,
    required: true
  },
  password: {
    type: String,
    required: true
  },
  pic: {
    type: String,
    required: true,
    default: defaultImg
  }

}, {
  timestamps: true
})

const User = mongoose.model('User', userModel)
module.exports = User